import axios from 'axios';

const API_URL = 'https:localhost:8000';
export const addNewTodo = (data) => async(dispatch) => {
    try{
        await axios.post(`${API_URL}/todos`,{data})
    } catch (error){
        console.log('error while calling addnewtodo api');
    }


}
